
'use strict';

const siteDeployClass = require('./lib/site-deploy');
var siteDeploy = new siteDeployClass({ "workingDir" : process.cwd(), "relativeImportUrl" : "/v1/import" });
var deployId = process.env.DEPLOY_ID || 'none';

siteDeploy.deploy().then( () => {
    console.log('Deployment of site content to persistency done [Deployment Id: ' + deployId + ']');
}).catch(err => {
    console.log('Deployment of site content to persistency failed [Deployment Id: ' + deployId + ']');
});
