
'use strict';

const independentDeployClass = require('./lib/independent-deploy');

class Deployer {
    constructor() {}
    deployIndpendentAppContent(workingDir) {
        return new Promise((resolve, reject) => {
            var independentDeploy = new independentDeployClass({ "workingDir" : workingDir, "relativeImportUrl" : "/v1/import" });
            independentDeploy.deploy().then( () => {
                resolve();
            }).catch(err => {
                reject(err);
            });
        });
    }
}

module.exports = Deployer;

